pragma solidity ^0.4.18;

import "./TixicoToken.sol";

contract TokenFaucet {
    // events
    event LogTokensTransferred(address demander, uint amount);
    event LogTokensDeposited(address sender, uint amount);

    // variables
    address owner;
    address tokenAddress;
    uint available = 0;
    uint demanded = 0;
    uint total = 0;

    // @notice constructor
    // @param _tixicoAddress sets the token address
    function TokenFaucet() public {
        owner = msg.sender;
    }

    function getTokenAddress() public view returns (address) {
        return tokenAddress;
    }

    // @notice sets the address for the token
    // @param token is the to be address for the token
    function setTokenAddress(address token) public {
        require(owner == msg.sender);

        tokenAddress = token;
    }

    // @notice supplies the demander with token if possible
    // @param amount sets the demanded amount for this call
    function demandTokens(uint amount) public {
        require(demanded + amount >= demanded);
        require(available > amount);

        demanded += amount;
        require(demanded < total);
        available = total - demanded;

        ERC20Interface token = ERC20Interface(tokenAddress);
        require(token.transfer(msg.sender, amount) == true);

        LogTokensTransferred(msg.sender, amount);
    }

    // @notice deposits tokens for distribution in the contract
    // @param amount sets the amount to be deposited
    function depositTokens(uint amount) public {
        require(total + amount > total);
        require(amount > 0);
        require(msg.sender == owner);

        total += amount;
        require(demanded < total);
        available = total - demanded;

        ERC20Interface token = ERC20Interface(tokenAddress);
        require(token.transferFrom(msg.sender, address(this), amount) == true);

        LogTokensDeposited(msg.sender, amount);
    }
}
